import React from 'react';
import ReactDOM from 'react-dom';

const TodoStatusFilter = () => {
    return (
        <div>
            <input type="button" value={'all'}/>
            <input type="button" value={'active'}/>
            <input type="button" value={'done'}/>


        </div>
    )
}



const TodoSearch = () => {
    return (
        <div>
            <input type="text"/>
        </div>
    )
}

const TodoList = ({todos}) => {
    const elements = todos.map((item) => {
        return <li key={item.id}><TodolistItem label={item.label}/></li>
    })
    return (
        <div>
            <ul>
                {elements}
            </ul>
        </div>
    )
}

const TodolistItem = ({label}) => {
    return (
        <span>{label}</span>
    )
}

const TodoHeader = () => {
    return (
        <div>
            <TodoSearch />
            <TodoStatusFilter />
        </div>
    )
};

const App  = () => {
    const todos = [
        {id: 1,label: 'Drink Coffee',done: false,important: false},
        {id: 2,label: 'Lear React',done: false,important: false},
        {id: 3,label: 'Drink vodka',done: false,important: false},
        {id: 4,label: 'Drink cola',done: false,important: false},

    ]
    return (
        <div className={'wrapper'}>
            <TodoHeader />
            <TodoList todos={todos}/>
        </div>
    )
}
ReactDOM.render(<App />,document.getElementById('root'));
